import React, { useState } from 'react';
import Swal from 'sweetalert2'
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Label, Input, FormGroup } from 'reactstrap';
import Upimg from './Upimg';





function Index() {
    const [modal, setModal] = useState(false);
    const toggle = () => setModal(!modal);
    const navigator = useNavigate()
    const [data, setData] = useState({
        name: '',
        nameProduc: '',
        description: '',
        price: '',
        amount: '',
        selectedFile: null
    })


    function handle(e) {
        e.preventDefault();
        const newdata = { ...data }
        newdata[e.target.id] = e.target.value
        setData(newdata)
        console.log(newdata)

    }

    // function readfile($event) {
    //     //this.setState({ selectedFile: event.target.files[0] });
    //     //console.log(event.target);
    //     console.log($event);
    // }

    // // On file select (from the pop up)
    // function onFileChange (event) {

    //     // Update the state
    //     console.log(event);

    // };


    const handleFileSelected = (e) => {
        const files = Array.from(e.target.files)
        console.log("files:", files);

        if( files && files.length > 0)
        {
            const newdata = { ...data }
            newdata["selectedFile"] = files[0];
            setData(newdata)
        }

      }


    const url = 'http://localhost:3000/api/product/create'

    function Enviar() {


        const formData = new FormData();
        formData.append("name", data.selectedFile);
        formData.append("nameProduc", data.nameProduc);
        formData.append("description", data.description);
        formData.append("price", data.price);
        formData.append("amount", data.amount);

        axios.post(url,formData )
            .then(res => {

                console.log(res)
                if(res.status === 200)
                {
                    Swal.fire({
                        title: 'Que bien',
                        text: "Se agrego nuevo producto!",
                        icon: 'success',
                        confirmButtonColor: '#0e46ff',
                        confirmButtonText: 'Okay'
                      }).then((result) => {
                        if (result.isConfirmed) {
                            window.location.replace('/Index');
                        }
                      })
                }else{
                    Swal.fire(
                        'ATENCIÃ“N',
                        'Ha ocurrido un error al guardar la imagen, reintente',
                        'warning'
                    );
                }
            })

        toggle(false);

    }


    return (
        <div class="container-xxl w-100 mt-5 d-lg-block col-md-5 col-lg-7 col-xl-6 bg-light rounder shadow">
            <div class="row align-items-stretch">
                <nav class="navbar navbar-light bg-light">
                    <div class="container-fluid">
                        <a class="navbar-brand text-primary" href="#">
                            <svg xmlns="http://www.w3.org/2000/svg" width="30" height="24" fill="currentColor" class="bi bi-code-slash" viewBox="0 0 16 16">
                                <path d="M10.478 1.647a.5.5 0 1 0-.956-.294l-4 13a.5.5 0 0 0 .956.294l4-13zM4.854 4.146a.5.5 0 0 1 0 .708L1.707 8l3.147 3.146a.5.5 0 0 1-.708.708l-3.5-3.5a.5.5 0 0 1 0-.708l3.5-3.5a.5.5 0 0 1 .708 0zm6.292 0a.5.5 0 0 0 0 .708L14.293 8l-3.147 3.146a.5.5 0 0 0 .708.708l3.5-3.5a.5.5 0 0 0 0-.708l-3.5-3.5a.5.5 0 0 0-.708 0z" />
                            </svg>
                        </a>
                        <div class="dropdown">

                            <button class="btn btn-outline-primary dropdown-toggle" type="button" id="dropdownMenu2" data-bs-toggle="dropdown" aria-expanded="false">
                                <svg xmlns="http://www.w3.org/2000/svg" width="30" height="24" fill="currentColor" class="bi bi-folder" viewBox="0 0 16 16">
                                    <path d="M.54 3.87.5 3a2 2 0 0 1 2-2h3.672a2 2 0 0 1 1.414.586l.828.828A2 2 0 0 0 9.828 3h3.982a2 2 0 0 1 1.992 2.181l-.637 7A2 2 0 0 1 13.174 14H2.826a2 2 0 0 1-1.991-1.819l-.637-7a1.99 1.99 0 0 1 .342-1.31zM2.19 4a1 1 0 0 0-.996 1.09l.637 7a1 1 0 0 0 .995.91h10.348a1 1 0 0 0 .995-.91l.637-7A1 1 0 0 0 13.81 4H2.19zm4.69-1.707A1 1 0 0 0 6.172 2H2.5a1 1 0 0 0-1 .981l.006.139C1.72 3.042 1.95 3 2.19 3h5.396l-.707-.707z" />
                                </svg>
                                Pages
                            </button>
                            <ul class="dropdown-menu" aria-labelledby="dropdownMenu2">
                                <li><a class="dropdown-item" type="button">Login</a></li>
                                <li><a class="dropdown-item" type="button">Sign Up</a></li>
                                <li><a class="dropdown-item" type="button">Reset Pass</a></li>
                            </ul>
                        </div>
                        <button onClick={toggle} class="btn btn-outline-primary" href="#">
                            <svg xmlns="http://www.w3.org/2000/svg" width="30" height="24" fill="currentColor" class="bi bi-bag" viewBox="0 0 16 16">
                                <path d="M8 1a2.5 2.5 0 0 1 2.5 2.5V4h-5v-.5A2.5 2.5 0 0 1 8 1zm3.5 3v-.5a3.5 3.5 0 1 0-7 0V4H1v10a2 2 0 0 0 2 2h10a2 2 0 0 0 2-2V4h-3.5zM2 5h12v9a1 1 0 0 1-1 1H3a1 1 0 0 1-1-1V5z" />
                            </svg>
                            Producto
                        </button>
                        <div class="dropdown">
                            <button class="btn btn-outline-primary dropdown-toggle" type="button" id="dropdownMenu2" data-bs-toggle="dropdown" aria-expanded="false">
                                <svg xmlns="http://www.w3.org/2000/svg" width="30" height="24" fill="currentColor" class="bi bi-person" viewBox="0 0 16 16">
                                    <path d="M8 8a3 3 0 1 0 0-6 3 3 0 0 0 0 6zm2-3a2 2 0 1 1-4 0 2 2 0 0 1 4 0zm4 8c0 1-1 1-1 1H3s-1 0-1-1 1-4 6-4 6 3 6 4zm-1-.004c-.001-.246-.154-.986-.832-1.664C11.516 10.68 10.289 10 8 10c-2.29 0-3.516.68-4.168 1.332-.678.678-.83 1.418-.832 1.664h10z" />
                                </svg>
                                Montse
                            </button>
                            <ul class="dropdown-menu" aria-labelledby="dropdownMenu2">
                                <li><button class="dropdown-item" type="button">Profile</button></li>
                                <li><button class="dropdown-item" type="button">Setting</button></li>
                                <li><a href="/" class="dropdown-item" type="button">Logout</a></li>
                            </ul>
                        </div>
                    </div>
                </nav>
            </div>
            <h1 className="text-dark">Productos</h1>
            <Upimg></Upimg>
            <div>
                <form>
                    <Modal isOpen={modal} >
                        <ModalHeader className="text-primary">Subir Producto</ModalHeader>
                        <ModalBody>
                            <form className="was-validated" noValidate  >
                                <FormGroup>
                                    <div>
                                        <Label>Img</Label>
                                        <input className="fw-bold" type="file" name='file' class="form-control" onChange={handleFileSelected} id="name"  aria-label="Upload" required></input>
                                    </div>
                                    <div>
                                        <Label for="price">Titulo</Label>
                                        <input type="text" className="form-control" onChange={(e) => handle(e)} id="nameProduc" value={data.nameProduc} placeholder="titulo" required ></input>
                                    </div>
                                    <div>
                                        <Label for="price">Description</Label>
                                        <input type="text" className="form-control" onChange={(e) => handle(e)} id="description" value={data.description} placeholder="descriptions" required ></input>
                                    </div>
                                    <div>
                                        <Label for="Stock">Precio</Label>
                                        <input type="text" className="form-control" onChange={(e) => handle(e)} id="price" value={data.price} placeholder="precio" required></input>
                                    </div>
                                    <div>
                                        <Label for="Stock">Stock</Label>
                                        <input type="text" className="form-control" onChange={(e) => handle(e)} id="amount" value={data.amount} placeholder="Stock" required></input>
                                    </div>
                                </FormGroup>
                            </form>
                        </ModalBody>
                        <ModalFooter>
                            <Button type="submit" onClick={Enviar} color="primary"  >Guardar</Button>
                            <Button color="secondary" onClick={toggle}>Cancel</Button>
                        </ModalFooter>
                    </Modal>
                </form>
            </div>
        </div>
    );
}

export default Index;
