import React, { useState } from 'react';
import Swal from 'sweetalert2'
import axios from "axios";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Label, Input, FormGroup } from 'reactstrap';
import Upimg from './Upimg';
import { Link } from 'react-router';

function Metodos() {
    const [modaladd, setModaladd] = useState(false);
    const add = () => setModaladd(!modaladd);
    const [modalupdate, setModalupdate] = useState(false);
    const update = () => setModalupdate(!modalupdate);
    const [modaldelet, setModaldelet] = useState(false);
    const delet = () => setModaldelet(!modaldelet);
    const [data1, setData1] = useState({
        name: '',
        nameProduc: '',
        description: '',
        price: '',
        amount: '',
        selectedFile: null
    })
    const [data, setData] = useState({
        id: '',
    })

    const [data2, setData2] = useState({
        name: '',
        nameProduc: '',
        description: '',
        price: '',
        amount: '',
        selectedFile: null
    })


    function handle(e) {
        e.preventDefault();
        const newdata = { ...data1 }
        newdata[e.target.id] = e.target.value
        setData1(newdata)
        console.log(newdata)

    }

    function handledelet(d) {
        d.preventDefault();
        const newdata = { ...data }
        newdata[d.target.id] = d.target.value
        setData(newdata)
        console.log(newdata)

    }


    function handleupdate(u) {
        u.preventDefault();
        const newdata = { ...data2 }
        newdata[u.target.id] = u.target.value
        setData2(newdata)
        console.log(newdata)

    }

    const handleFileSelectedupdate = (u) => {
        const files = Array.from(u.target.files)
        console.log("files:", files);

        if (files && files.length > 0) {
            const newdata = { ...data1 }
            newdata["selectedFile"] = files[0];
            setData1(newdata)
        }

    }

    const handleFileSelected = (e) => {
        const files = Array.from(e.target.files)
        console.log("files:", files);

        if (files && files.length > 0) {
            const newdata = { ...data1 }
            newdata["selectedFile"] = files[0];
            setData1(newdata)
        }

    }

    const urlupdate = 'http://localhost:3000/api/product/update'

    function Enviarupdate() {
        const formData = new FormData();
        formData.append("id", data2.id);
        formData.append("nameProduc", data2.nameProduc);
        formData.append("description", data2.description);
        formData.append("price", data2.price);
        formData.append("amount", data2.amount);

        axios.put(urlupdate, formData)
            .then(res => {
                if (res.status === 200) {
                    Swal.fire({
                        title: 'Excellent',
                        text: "The product was updated!",
                        icon: 'success',
                        confirmButtonColor: '#0e46ff',
                        confirmButtonText: 'Okay'
                    }).then((result) => {
                        if (result.isConfirmed) {
                            window.location.replace('/Index');
                        }
                    })
                } else {
                    Swal.fire(
                        'ATENCIÓN',
                        'Ha ocurrido un error al actualizar, reintente',
                        'warning'
                    );
                }
            })

        update(false);

    }

    const urldelet = 'http://localhost:3000/api/product/delete'

    const EnviarDelet = (d) => {

        axios.delete(urldelet, {
            data: data
        })
            .then(res => {
                setData(res.data)


            })
            .catch(err => {
                console.log(err)
            })
        Swal.fire({
            title: 'Datos Eliminados!',
            text: "",
            icon: 'success',
            confirmButtonColor: '#0e46ff',
            confirmButtonText: 'Okay'
        }).then((result) => {
            if (result.isConfirmed) {
                window.location.replace('/Index');
            }
        })
        delet(false);

    }


    const url = 'http://localhost:3000/api/product/create'

    function Enviar() {


        const formData = new FormData();
        formData.append("name", data1.selectedFile);
        formData.append("nameProduc", data1.nameProduc);
        formData.append("description", data1.description);
        formData.append("price", data1.price);
        formData.append("amount", data1.amount);

        axios.post(url, formData)
            .then(res => {

                if (res.status === 200) {
                    Swal.fire({
                        title: 'Thats great',
                        text: "New product added!",
                        icon: 'success',
                        confirmButtonColor: '#0e46ff',
                        confirmButtonText: 'Okay'
                    }).then((result) => {
                        if (result.isConfirmed) {
                            window.location.replace('/Index');
                        }
                    })
                } else {
                    Swal.fire(
                        'ALERT',
                        'An error occurred while saving the image, please try again',
                        'warning'
                    );
                }
            })

        add(false);

    }


    return (
        <div >
            <div >
                <nav class="navbar navbar-light bg-light">
                    <div class="container-fluid">
                        <div class="dropdown">
                            <button class="btn btn-outline-secondary" href="#">
                                <svg xmlns="http://www.w3.org/2000/svg" width="30" height="24" fill="currentColor" class="bi bi-bag-plus" viewBox="0 0 16 16">
                                <path fill-rule="evenodd" d="M8 7.5a.5.5 0 0 1 .5.5v1.5H10a.5.5 0 0 1 0 1H8.5V12a.5.5 0 0 1-1 0v-1.5H6a.5.5 0 0 1 0-1h1.5V8a.5.5 0 0 1 .5-.5z" />
                                    <path d="M8 1a2.5 2.5 0 0 1 2.5 2.5V4h-5v-.5A2.5 2.5 0 0 1 8 1zm3.5 3v-.5a3.5 3.5 0 1 0-7 0V4H1v10a2 2 0 0 0 2 2h10a2 2 0 0 0 2-2V4h-3.5zM2 5h12v9a1 1 0 0 1-1 1H3a1 1 0 0 1-1-1V5z" />
                                </svg><span><a a className="text-muted" href="/Reporte">Cubículo</a></span>
                            </button>
                        </div>
                        <div class="dropdown">
                            <button class="btn btn-outline-secondary" href="#">
                                <span><a a className="text-primary" href="/Bitacoras">Proyectores</a></span>

                            </button>
                        </div>
                        <div class="dropdown">
                            <button  class="btn btn-outline-secondary" href="#">
                                <span><a a className="text-primary" href="/Equipos">Equipos</a></span>

                            </button>
                        </div>
                        <div class="dropdown">
                        <button  class="btn btn-outline-secondary" type="button"><span><a a className="text-primary" href="/Uso">Usos</a></span>
                                </button>
                        </div>
                        <div class="dropdown">
                        <button  class="btn btn-outline-secondary" href="#">
                                <span><a a className="text-primary" href="/Registros">Mantenimiento</a></span>
                            </button>
                        </div>


                        <div class="dropdown">
                            <button class="btn btn-outline-black dropdown-toggle" type="button" id="dropdownMenu2" data-bs-toggle="dropdown" aria-expanded="false">
                                <svg xmlns="http://www.w3.org/2000/svg" width="30" height="24" fill="currentColor" class="bi bi-person" viewBox="0 0 16 16">
                                    <path d="M8 8a3 3 0 1 0 0-6 3 3 0 0 0 0 6zm2-3a2 2 0 1 1-4 0 2 2 0 0 1 4 0zm4 8c0 1-1 1-1 1H3s-1 0-1-1 1-4 6-4 6 3 6 4zm-1-.004c-.001-.246-.154-.986-.832-1.664C11.516 10.68 10.289 10 8 10c-2.29 0-3.516.68-4.168 1.332-.678.678-.83 1.418-.832 1.664h10z" />
                                </svg>

                            </button>
                            <ul class="dropdown-menu" aria-labelledby="dropdownMenu2">
                                <li><a href="/" class="dropdown-item" type="button">Cerrar sesión</a></li>
                            </ul>
                        </div>
                    </div>
                </nav>

            </div>
            <Upimg></Upimg>

        </div>
    );
}

export default Metodos;
