import '../asset/style/card.css'



const Card = (props) => {
    const [modal, setModal] = useState(false);
    const toggle = () => setModal(!modal);
    const navigator = useNavigate()
    const [data, setData] = useState({
        name: '',
        nameProduc: '',
        description: '',
        price: '',
        amount: '',
        selectedFile: null
    })


    function handle(e) {
        e.preventDefault();
        const newdata = { ...data }
        newdata[e.target.id] = e.target.value
        setData(newdata)
        console.log(newdata)

    }

    // function readfile($event) {
    //     //this.setState({ selectedFile: event.target.files[0] });
    //     //console.log(event.target);
    //     console.log($event);
    // }

    // // On file select (from the pop up)
    // function onFileChange (event) {

    //     // Update the state
    //     console.log(event);

    // };


    const handleFileSelected = (e) => {
        const files = Array.from(e.target.files)
        console.log("files:", files);

        if( files && files.length > 0)
        {
            const newdata = { ...data }
            newdata["selectedFile"] = files[0];
            setData(newdata)
        }

      }


    const url = 'http://localhost:3000/api/product/update'

    function Enviar() {


        const formData = new FormData();
        formData.append("name", data.selectedFile);
        formData.append("nameProduc", data.nameProduc);
        formData.append("description", data.description);
        formData.append("price", data.price);
        formData.append("amount", data.amount);

        axios.post(url,formData )
            .then(res => {

                console.log(res)
                if(res.status === 200)
                {
                    Swal.fire({
                        title: 'Que bien',
                        text: "Se agrego nuevo producto!",
                        icon: 'success',
                        confirmButtonColor: '#0e46ff',
                        confirmButtonText: 'Okay'
                      }).then((result) => {
                        if (result.isConfirmed) {
                            window.location.replace('/Index');
                        }
                      })
                }else{
                    Swal.fire(
                        'ATENCIÓN',
                        'Ha ocurrido un error al guardar la imagen, reintente',
                        'warning'
                    );
                }
            })

        toggle(false);

    }

    return (
        <div className="container-Card border-primary">
            <img src={props.name} class="card-img-top"></img><br /><br/>
            <div className="card-title text-black"><h3>{props.nameProduc}</h3></div><br/>
            <div className="card-price "><span className="text-black fw-bold fs-5">Precio: </span> {props.price} MXN</div><br/>
            <div className="card-amount"><span className="text-black fw-bold fs-5">Stock: </span>{props.amount}</div><br/>
            <div className="card-description"><span className="text-black fw-bold fs-5">Descripcion: </span>{props.description}</div>

            <button onClick={''} class="btn btn-outline-black" href="#">
                <svg xmlns="http://www.w3.org/2000/svg" width="30" height="24" fill="currentColor" class="bi bi-bag" viewBox="0 0 16 16">
                <path d="M8 1a2.5 2.5 0 0 1 2.5 2.5V4h-5v-.5A2.5 2.5 0 0 1 8 1zm3.5 3v-.5a3.5 3.5 0 1 0-7 0V4H1v10a2 2 0 0 0 2 2h10a2 2 0 0 0 2-2V4h-3.5zM2 5h12v9a1 1 0 0 1-1 1H3a1 1 0 0 1-1-1V5z" />
                </svg>
                    Edit
            </button>

        </div>


    )
}

export default Card;
