import React, { useEffect, useState } from 'react';
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import {makeStyles} from '@material-ui/core/styles';
import {Table, TableContainer, TableHead, TableCell, TableBody, TableRow, Modal, Button, TextField} from '@material-ui/core';
import {Edit, Delete} from '@material-ui/icons';





const url='http://localhost:3000/api/user/';

const useStyles = makeStyles((theme) => ({
    modal: {
        position: 'absolute',
        width: 400,
        backgroundColor: theme.palette.background.paper,
        border: '2px solid #000',
        boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 3),
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)'
    },
    iconos:{
        cursor: 'pointer'
    },
    inputMaterial:{
        width: '100%'
    }
}));

function Reporte () {

    const styles= useStyles();

    const [data, setData] = useState([]);

    const [insertar, setInsertar] = useState(false);
    const [editar, setEditar] = useState(false);
    const [eliminar, setEliminar] = useState(false);

    const [user, setUser] = useState({
        id: '',
        name: '',
        nameProduc: '',
        description: '',
        price: '',
        amount: ''
    })

    const handleChange=e=>{
        const {name, value}=e.target;
        setUser(prevState=>({
            ...prevState,
            [name]: value
        }))
        console.log(user);
    }

    const peticionGet=async()=>{
        await axios.get('http://localhost:3000/api/product/view')
            .then(response=>{
                //console.log(response.data.statusMessage.rows)
               setData(response.data.message.rows);
            })
    }
//Cambiar en la cuestipnd e los parametros
    const peticionPost=async()=>{
        await axios.post(`${url}register?username=${user.username}&email=${user.email}&password=${user.password}&phone_number=${user.phone_number}`)
            .then(response=>{
                console.log(response);
                //setData(data.concat(response.data))
                modalInsertar()
                peticionGet()
            })
    }
//Correguir
    const peticionPut=async()=>{
        await axios.put(`${url}update?id=${user.id}&username=${user.username}&email=${user.email}&password=${user.password}&phone_number=${user.phone_number}`)
            .then(response=>{
                // var dataNueva=data;
                // dataNueva.map(selectUser=>{
                //     if(user.id===selectUser.id){
                //         selectUser.username=user.username;
                //         selectUser.email=user.email;
                //         selectUser.password=user.password;
                //         selectUser.phone_number=user.phone_number;
                //     }
                // })
               // setData(dataNueva);
                modalEditar();
                peticionGet()
                console.log(response);
            })
    }
//Correguir
    const peticionDelete=async()=>{
        await axios.delete(`${url}delete?id=${user.id}`)
            .then(response=>{
                console.log(response);
                //setData(data.filter(selectUser=>selectUser.id!==user.id));
                modalEliminar();
                peticionGet()
            });
    }


    const modalInsertar=()=>{
        setInsertar(!insertar);
    }

    const modalEditar=()=>{
        setEditar(!editar);
    }

    const modalEliminar=()=>{
        setEliminar(!eliminar);
    }

    const seleccionCliente=(selectUser, caso)=>{
        setUser(selectUser);
        (caso==='Editar')?modalEditar():modalEliminar()
    }

    useEffect(async()=>{
        await peticionGet();
    },[])


    const cuerpoInsertar=(
        <div className={styles.modal}>
            <h3>Registrar Usuario</h3>
            <TextField type="text" name="username" className={styles.inputMaterial} label="Username:" onChange={handleChange}/>
            <br />
            <TextField type="email" name="email" className={styles.inputMaterial} label="Email:" onChange={handleChange}/>
            <br />
            <TextField type="password" name="password" className={styles.inputMaterial} label="Password: " onChange={handleChange}/>
            <br />
            <TextField type="number" name="phone_number" className={styles.inputMaterial} label="Phone Number: " onChange={handleChange}/>
            <br /><br/>
            <div align="right">
                <Button type="button" class="btn btn-success" onClick={()=>peticionPost()}>Registrar</Button>
                <Button type="button" class="btn btn-danger" onClick={()=>modalInsertar()}>Cancelar</Button>
            </div>
        </div>
    )

    const cuerpoEditar=(
        <div className={styles.modal}>
            <h3>Editar Usuario</h3>
            <TextField type="text" name="username" className={styles.inputMaterial} label="Username: " onChange={handleChange} value={user && user.username}/>
            <br />
            <TextField type="email" name="email" className={styles.inputMaterial} label="Email: " onChange={handleChange} value={user && user.email}/>
            <br />
            <TextField type="password" name="password" className={styles.inputMaterial} label="Password: " onChange={handleChange} value={user && user.password}/>
            <br />
            <TextField type="number" name="phone_number" className={styles.inputMaterial} label="Password: " onChange={handleChange} value={user && user.phone_number}/>
            <br /><br/>
            <div align="right">
                <Button type="button" class="btn btn-info" onClick={()=>peticionPut()}>Actualizar</Button>
                <Button type="button" class="btn btn-danger" onClick={()=>modalEditar()}>Cancelar</Button>
            </div>
        </div>
    )

    const cuerpoEliminar=(
        <div className={styles.modal}>
            <p>Confirmar eliminación: <b>{user && user.id}</b> ? </p>
            <div align="right">
                <Button type="button" class="btn btn-success" onClick={()=>peticionDelete()} >Sí</Button>
                <Button type="button" class="btn btn-danger" onClick={()=>modalEliminar()}>No</Button>
            </div>
        </div>
    )

    return (
        <main>
            <br />
            <Button type="button" class="btn btn-success" onClick={()=>modalInsertar()}>Agregar</Button>
            <br /><br />
            <TableContainer>
                <Table class="table table-success table-striped">
                    <TableHead>
                        <TableRow >
                            <TableCell class="table-dark">Imagen</TableCell>
                            <TableCell class="table-dark">nameProduct</TableCell>
                            <TableCell class="table-dark">description</TableCell>
                            <TableCell class="table-dark">price</TableCell>
                            <TableCell class="table-dark">amout</TableCell>
                            <TableCell class="table-dark">Opciones</TableCell>
                        </TableRow>
                    </TableHead>

                    <TableBody>
                        {data.map(selectUser=>(
                            <TableRow key={selectUser.id}>
                                <TableCell class="table-dark">{selectUser.name}</TableCell>
                                <TableCell class="table-dark">{selectUser.nameProduc}</TableCell>
                                <TableCell class="table-dark">{selectUser.description}</TableCell>
                                <TableCell class="table-dark">{selectUser.price}</TableCell>
                                <TableCell class="table-dark">{selectUser.amount}</TableCell>

                                <TableCell class="table-dark">
                                    <Edit  className={styles.iconos} onClick={()=>seleccionCliente(selectUser, 'Editar')}/>
                                    &nbsp;&nbsp;&nbsp;
                                    <Delete  className={styles.iconos} onClick={()=>seleccionCliente(selectUser, 'Eliminar')}/>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>


            <Modal
                open={insertar}
                onClose={modalInsertar}>
                {cuerpoInsertar}
            </Modal>

            <Modal
                open={editar}
                onClose={modalEditar}>
                {cuerpoEditar}
            </Modal>

            <Modal
                open={eliminar}
                onClose={modalEliminar}>
                {cuerpoEliminar}
            </Modal>
        </main>
    )
}

export default Reporte;
