import React, { useState } from "react";
import { useForm } from "react-hook-form";
import "../asset/style/Log_in.css";
import Swal from 'sweetalert2'
import axios from "axios";
import { useNavigate } from "react-router-dom";


function Login() {
    const navigator = useNavigate()
    const data = useState({
        email: '',
        password: '',
        validat: ''
    })

    const url = 'http://localhost:3000/api/user/login'

    const { handleSubmit, register, formState: { errors } } = useForm();

    const onSubmit = values => {
        const data = values;

        console.log(data);
        axios.post(url, {
            email: data.email,
            password: data.password,
            validat: data.validat
        })
            .then(res => {
                if (res.request.status === 200) {
                    if (data.email === "nadanada5463453dfdg@ids.upchiapas.edu.mx") {
                        Swal.fire(
                            'Bienvenido',
                            '' + res.data.data.name + '',
                            'success'
                        )
                        navigator('/Shop')
                    }
                    else {
                        Swal.fire(
                            'Bienvenido',
                            '' + res.data.data.name + '',
                            'success'
                        )
                        navigator('/Index')
                    }

                }
            })
            .catch(err => {
                Swal.fire(
                    'Error!',
                    '' + err.response.data.error + '',
                    'error'
                )



            })




    }






    return (
        <section class="h-100 gradient-form">
            <div className="container py-5 h-100">
                <div className="row align-items-stretch">
                    <div className="row d-flex justify-content-center align-items-center h-100">
                    </div>
                    <div className="col p-5 rounder-end">

                        <h2 className="fw-bold text-center py-5  text-light">Welcome</h2>

                        <form className="was-validated" noValidate onSubmit={handleSubmit(onSubmit)}>
                            <div className="mb-4">
                                <label className="form-label text-light ">Email</label>
                                <input type="text" className="form-control" id="email" placeholder="Email" required {...register("email", {
                                    required: {
                                        value: true,
                                        message: "Obligatory field",
                                    },
                                    pattern: {
                                        value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                                        message: "Invalido email"
                                    }
                                })}></input>
                                {errors.email && <span className="text-danger">{errors.email.message}</span>}
                            </div>
                            <div className="mb-4">
                                <label className="form-label text-light">Password</label>
                                <input type="password" className="form-control" placeholder="Password" required {...register("password", {
                                    required: {
                                        value: true,
                                        message: "Obligatory field",
                                    },
                                    minLength: {
                                        value: 8,
                                        message: "Password with a minimum of 8 characters"
                                    }
                                })}></input>
                                {errors.password && <span className="text-danger">{errors.password.message}</span>}
                            </div>
                            <div className="my-3">
                                <span><a href="/Recover_pass">Forgot password?</a></span>
                            </div>
                            <div className="d-grid">
                                <button type="submit" className="btn btn-primary">Log in</button>
                            </div>

                            <div className="my-3">
                                <span className="fw-bold text-light">You do not have an account? <a href="/Sign_up">Create account</a></span>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </section>
    );
}

export default Login;
